
var FLOAT_MARGIN = 0.000001;

module.exports = {
    ceil:   function (val) {
        return Math.ceil (val - FLOAT_MARGIN);
    },
    floor:  function (val) {
        return Math.floor (val + FLOAT_MARGIN);
    },
    equal:  function (able, baker) {
        return Math.abs (able - baker) < FLOAT_MARGIN;
    },
    clean:  function (val, target) {
        if (target !== undefined) {
            return Math.abs (target - val) < FLOAT_MARGIN ? target : val;
        }
        var rounded = Math.round (val);
        return Math.abs (val - rounded) < FLOAT_MARGIN ? rounded : val;
    }
};
