
function StringInput (labelText, document, config, callback) {
    var elem = document.createElement ('input');
    elem.setAttribute ('type', 'text');
    if (config.width)
        elem.style.width = config.width + 'px';

    // control events
    var self = this;
    elem.on ('input', function (event) {
        self.value = this.value;
        callback (this.value);
    });

    // trap keypresses when we have focus
    elem.on ('keydown', function (event) {
        event.stopPropagation();
        return true;
    });

    // wrap the input in a label and return
    var label = document.createElement ('label');
    label.appendChild (document.createTextNode (labelText));
    label.appendChild (elem);
    this.elem = label;
}

StringInput.prototype.set = function (value) {
    this.elem.firstElementChild.value = value;
};

StringInput.prototype.enable = function (isEnabled) {

};

module.exports = StringInput;
